function forceClose() {
    $("#nav_info").removeClass("activated")
}
function mobNav() {
    var e = document.getElementById("navigation");
    "nav mobile-nav" === e.className ? e.className += " on" : e.className = "nav mobile-nav"
}
$(document).ready(function() {
    $("#intro_rotate").rotaterator({
        fadeSpeed: 500,
        pauseSpeed: 2500
    }),
    $(".animsition").animsition({
        inClass: "fade-in",
        outClass: "fade-out",
        inDuration: 1500,
        outDuration: 800,
        linkElement: ".animsition-link",
        loading: !0,
        loadingParentElement: "html",
        loadingClass: "animsition-loading",
        loadingInner: "",
        timeout: !1,
        timeoutCountdown: 5e3,
        onLoadEvent: !0,
        browser: ["animation-duration", "-webkit-animation-duration"],
        overlay: !1,
        overlayClass: "animsition-overlay-slide",
        overlayParentElement: "body",
        transition: function(e) {
            window.location.href = e
        }
    }),
    $(".js-tilt,.intro-tilt").tilt({
        maxTilt: 20,
        perspective: 2e3
    })
});
var delayInMilliseconds = 5e3;
setTimeout(function() {
    $("#nav_info").addClass("activated")
}, delayInMilliseconds);
var closeaftersomeseconds = 1e4;
setTimeout(function() {
    $("#nav_info").removeClass("activated")
}, closeaftersomeseconds);



function validateForm() {
    var name = document.forms["contactForm"]["name"].value;
    var email = document.forms["contactForm"]["email"].value;
    var message = document.forms["contactForm"]["message"].value;
    var error = "";

    if (name == "") {
        error += "Please enter your name.\n";
    }

    if (email == "") {
        error += "Please enter your email address.\n";
    } else if (!validateEmail(email)) {
        error += "Please enter a valid email address.\n";
    }

    if (message == "") {
        error += "Please enter a message.\n";
    }

    if (error != "") {
        alert(error);
        return false;
    }
}

function validateEmail(email) {
    var re = /\S+@\S+\.\S+/;
    return re.test(email);
}
